import { useRouter } from "next/router";
import { FC, useRef } from "react";
import styled, { css } from "styled-components";

export interface ContainerProps {
  about?: string;
  music?: string;
  socials?: string;
  children?: React.ReactNode;
}

const aboutPageBorderImgUrl = ` url("data:image/png;base64,")
  28 / 28px / 0 stretch `;

const musicPageBorderImgUrl = ` url("data:image/png;base64,")
  28 / 28px / 0 stretch `;

const socialsPageBorderImgUrl = ` url("data:image/png;base64,")  28 / 28px / 0 stretch `;

let StyledMainDiv = styled.div<ContainerProps>`
  grid-area: canvas;
  font-size: 2.5rem;
  width: 100%;
  height: 100%;
  border-width: 12px;
  border-style: solid;
  border-image: ${(props) =>
    props.about === "true"
      ? aboutPageBorderImgUrl
      : 0 || props.music === "true"
      ? musicPageBorderImgUrl
      : 0 || props.socials === "true"
      ? socialsPageBorderImgUrl
      : 0};
  border-image-repeat: stretch repeat;
`;

export let ContentContainer: FC<ContainerProps> = ({
  about = "false",
  music = "false",
  socials = "false",
  children,
  ...props
}) => {
  const router = useRouter();

  return (
    <>
      <StyledMainDiv about={about} music={music} socials={socials}>
        {children}
      </StyledMainDiv>
    </>
  );
};
